import {makeAutoObservable} from "mobx";

class CanvasState {
    canvas = null;
    constructor() {
        makeAutoObservable(this);
    }

    //this function has name action cuz this function change state
    setTool(canvas) {
        this.tool = canvas;
    }
}

export default new CanvasState();
