import React, {useEffect, useRef} from 'react';
import "../styles/canvas.scss";
import {observer} from "mobx-react-lite";
import canvasState from "../store/canvasState";
import toolState from "../store/toolState";
import Brush from "../tools/Brush";
import {Button, Modal} from "react-bootstrap";

const Canvas = observer(() => {
    const canvasRef = useRef();
    const usernameRef = useRef();

    // При першому запуску програми
    useEffect(() => {
        canvasState.setCanvas(canvasRef.current);
        toolState.setTool(new Brush(canvasRef.current)); // як інструмент присваюємо пензлик
    }, []);
    // якщо масив залежностей пустий, то ця функція відпрацює лише один раз
    // тому при запуску програми ми будемо зберігати посилання на канвас

    const mouseDownHandler = () => {
        canvasState.pushToUndo(canvasRef.current.toDataURL())
    }

    const connectionHandler = () => {
        canvasState.setUsername(usernameRef.current.value);
    }

    return (
        <div className="canvas">
            <Modal show={true} onHide={() => {}}>
                <Modal.Header closeButton>
                    <Modal.Title>Enter your name</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <input type="text" ref={usernameRef}/>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => connectionHandler()}>
                        Join
                    </Button>
                </Modal.Footer>
            </Modal>
            <canvas onMouseDown={() => mouseDownHandler()} ref={canvasRef} width={600} height={400} />
        </div>
    );
});

export default Canvas;
